local function create_drawing_library()
    local drawing = {} drawing.__index = drawing
    function drawing.new(type)
        local self = setmetatable({}, drawing) self.Type = type self.Visible = true self.ZIndex = 1 self.Transparency = 1 self.Color = Color3.new(1, 1, 1) self.Size = UDim2.new(0, 100, 0, 100) self.Position = UDim2.new(0, 0, 0, 0) self.Filled = false self.Thickness = 1 self.Text = "" self.FontFace = custom_font self.TextSize = 14 self.Outline = false self.OutlineColor = Color3.new(0, 0, 0) self.Center = false self.Parent = nil
        if type == "Square" then
            self.Instance = Instance.new("Frame") self.Instance.BackgroundColor3 = self.Color self.Instance.BorderSizePixel = 0 self.Instance.Size = self.Size self.Instance.Position = self.Position self.Instance.BackgroundTransparency = self.Transparency self.Instance.ZIndex = self.ZIndex self.Instance.Parent = screenGui
            local button = Instance.new("TextButton") button.Size = UDim2.new(1, 0, 1, 0) button.BackgroundTransparency = 1 button.Text = "" button.ZIndex = self.ZIndex + 10 button.Parent = self.Instance
            self.MouseButton1Click = button.MouseButton1Click self.MouseButton1Down = button.MouseButton1Down self.MouseButton1Up = button.MouseButton1Up self.MouseEnter = button.MouseEnter self.MouseLeave = button.MouseLeave
        elseif type == "Text" then
            self.Instance = Instance.new("TextLabel") self.Instance.BackgroundTransparency = 1 self.Instance.TextColor3 = self.Color self.Instance.Text = self.Text self.Instance.FontFace = self.FontFace self.Instance.TextSize = self.TextSize self.Instance.Size = self.Size self.Instance.Position = self.Position self.Instance.ZIndex = self.ZIndex self.Instance.TextTransparency = self.Transparency self.Instance.TextXAlignment = self.Center and Enum.TextXAlignment.Center or Enum.TextXAlignment.Left self.Instance.Parent = screenGui
            local button = Instance.new("TextButton") button.Size = UDim2.new(1, 0, 1, 0) button.BackgroundTransparency = 1 button.Text = "" button.ZIndex = self.ZIndex + 10 button.Parent = self.Instance
            self.MouseButton1Click = button.MouseButton1Click self.MouseButton1Down = button.MouseButton1Down self.MouseButton1Up = button.MouseButton1Up self.MouseEnter = button.MouseEnter self.MouseLeave = button.MouseLeave
        end
        return self
    end
    function drawing:Remove() if self.Instance then self.Instance:Destroy() end end
    function drawing:AddListLayout(padding) if self.Instance then local uiListLayout = Instance.new("UIListLayout") uiListLayout.Padding = UDim.new(0, padding or 0) uiListLayout.SortOrder = Enum.SortOrder.LayoutOrder uiListLayout.Parent = self.Instance end end
    function drawing:MakeScrollable() if self.Instance then local scrollingFrame = Instance.new("ScrollingFrame") scrollingFrame.Size = self.Instance.Size scrollingFrame.Position = self.Instance.Position scrollingFrame.BackgroundTransparency = 1 scrollingFrame.ScrollBarThickness = 0 scrollingFrame.ZIndex = self.Instance.ZIndex scrollingFrame.Parent = self.Instance.Parent
            local button = Instance.new("TextButton") button.Size = UDim2.new(1, 0, 1, 0) button.BackgroundTransparency = 1 button.Text = "" button.ZIndex = self.Instance.ZIndex + 10 button.Parent = scrollingFrame
            self.MouseButton1Click = button.MouseButton1Click self.MouseButton1Down = button.MouseButton1Down self.MouseButton1Up = button.MouseButton1Up
            self.Instance.Size = UDim2.new(1, 0, 1, 0) self.Instance.Position = UDim2.new(0, 0, 0, 0) self.Instance.Parent = scrollingFrame
            self.Instance = scrollingFrame end end
    function drawing:RefreshScrolling() if self.Instance and self.Instance:IsA("ScrollingFrame") then end end
    function drawing:__newindex(key, value)
        rawset(self, key, value)
        if self.Instance then
            if key == "Visible" then self.Instance.Visible = value
            elseif key == "Transparency" then if self.Instance:IsA("Frame") then self.Instance.BackgroundTransparency = value elseif self.Instance:IsA("TextLabel") then self.Instance.TextTransparency = value end
            elseif key == "Color" then if self.Instance:IsA("Frame") then self.Instance.BackgroundColor3 = value elseif self.Instance:IsA("TextLabel") then self.Instance.TextColor3 = value end
            elseif key == "Size" then self.Instance.Size = value
            elseif key == "Position" then self.Instance.Position = value
            elseif key == "Text" then if self.Instance:IsA("TextLabel") then self.Instance.Text = value end
            elseif key == "FontFace" then if self.Instance:IsA("TextLabel") then self.Instance.FontFace = value end
            elseif key == "TextSize" then if self.Instance:IsA("TextLabel") then self.Instance.TextSize = value end
            elseif key == "Center" then if self.Instance:IsA("TextLabel") then self.Instance.TextXAlignment = value and Enum.TextXAlignment.Center or Enum.TextXAlignment.Left end
            elseif key == "Parent" then if value and value.Instance then self.Instance.Parent = value.Instance else self.Instance.Parent = screenGui end
            elseif key == "ZIndex" then self.Instance.ZIndex = value if self.Instance:FindFirstChildWhichIsA("TextButton") then self.Instance:FindFirstChildWhichIsA("TextButton").ZIndex = value + 10 end end
        end
    end
    drawing.Fonts = {Plex = custom_font, UI = custom_font, System = custom_font, Monospace = custom_font}
    return drawing
end
