local function create_tween_library()
    local tween = {}
    function tween.new(instance, tweenInfo, properties) local tweenService = game:GetService("TweenService") local tweenObject = tweenService:Create(instance.Instance, tweenInfo, properties) return {Play = function() tweenObject:Play() end, Pause = function() tweenObject:Pause() end, Cancel = function() tweenObject:Cancel() end} end
    return tween
end
