function utility.dragify(main, dragoutline, object)
    local start, objectposition, dragging, currentpos
    local function handleInput() dragging = true start = services.InputService:GetMouseLocation() dragoutline.Visible = true objectposition = object.Position end
    local function handleMove(input) if input.UserInputType == Enum.UserInputType.MouseMovement or input.UserInputType == Enum.UserInputType.Touch then if dragging then local delta = input.Position - start currentpos = UDim2.new(objectposition.X.Scale, objectposition.X.Offset + delta.X, objectposition.Y.Scale, objectposition.Y.Offset + delta.Y) dragoutline.Position = currentpos end end end
    local function handleEnd() if dragging then dragging = false dragoutline.Visible = false if currentpos then object.Position = currentpos end end end
    if main.MouseButton1Down then main.MouseButton1Down:Connect(handleInput) else utility.connect(services.InputService.InputBegan, function(input) if input.UserInputType == Enum.UserInputType.MouseButton1 or input.UserInputType == Enum.UserInputType.Touch then handleInput() end end) end
    utility.connect(services.InputService.InputChanged, handleMove)
    if main.MouseButton1Up then main.MouseButton1Up:Connect(handleEnd) else utility.connect(services.InputService.InputEnded, function(input) if input.UserInputType == Enum.UserInputType.MouseButton1 or input.UserInputType == Enum.UserInputType.Touch then handleEnd() end end) end
end
function utility.textlength(str, font, fontsize) local text = drawing.new("Text") text.Text = str text.FontFace = font text.TextSize = fontsize local textbounds = text.Instance.TextBounds text:Remove() return textbounds end
function utility.getcenter(sizeX, sizeY) return UDim2.new(0.5, -(sizeX / 2), 0.5, -(sizeY / 2)) end
function utility.table(tbl, usemt) tbl = tbl or {} local oldtbl = table.clone(tbl) table.clear(tbl) for i, v in next, oldtbl do if type(i) == "string" then tbl[i:lower()] = v else tbl[i] = v end end if usemt == true then setmetatable(tbl, {__index = function(t, k) return rawget(t, k:lower()) or rawget(t, k) end, __newindex = function(t, k, v) if type(k) == "string" then rawset(t, k:lower(), v) else rawset(t, k, v) end end}) end return tbl end
function utility.colortotable(color) local r, g, b = math.floor(color.R * 255), math.floor(color.G * 255), math.floor(color.B * 255) return {r, g, b} end
function utility.tabletocolor(tbl) return Color3.fromRGB(unpack(tbl)) end
function utility.round(number, float) return float * math.floor(number / float) end
function utility.getrgb(color) local r = color.R * 255 local g = color.G * 255 local b = color.B * 255 return r, g, b end
function utility.changecolor(color, number) local r, g, b = utility.getrgb(color) r, g, b = math.clamp(r + number, 0, 255), math.clamp(g + number, 0, 255), math.clamp(b + number, 0, 255) return Color3.fromRGB(r, g, b) end
function utility.nextflag() totalunnamedflags = totalunnamedflags + 1 return string.format("%.14g", totalunnamedflags) end
function utility.rgba(r, g, b, alpha) local rgb = Color3.fromRGB(r, g, b) local mt = table.clone(getrawmetatable(rgb)) setreadonly(mt, false) local old = mt.__index mt.__index = newcclosure(function(self, key) if key:lower() == "a" then return alpha end return old(self, key) end) setrawmetatable(rgb, mt) return rgb end
function utility.connect(signal, callback) local connection = signal:Connect(callback) if library.connections then table.insert(library.connections, connection) end return connection end
function utility.disconnect(connection) if library.connections then local index = table.find(library.connections, connection) connection:Disconnect() if index then table.remove(library.connections, index) end end end
